 public function import() {

        $excel_field_error = 0;

        $_SESSION['categorylist']=array();

        $this->load->language('sale/customer');
        //$this->load->model('sale/order');

        $data['heading_title'] = "Import Category Data";

        $data['entry_import'] = $this->language->get('Upload CSV File');

