public function cleanString($text) {
						// 1) convert   => a o
						$text = preg_replace("/[]/u","a",$text);
						$text = preg_replace("/[]/u","A",$text);
						$text = preg_replace("/[]/u","I",$text);
						$text = preg_replace("/[]/u","i",$text);
						$text = preg_replace("/[]/u","e",$text);
						$text = preg_replace("/[]/u","E",$text);
						$text = preg_replace("/[]/u","o",$text);
						$text = preg_replace("/[]/u","O",$text);
						$text = preg_replace("/[]/u","u",$text);
						$text = preg_replace("/[]/u","U",$text);
						$text = preg_replace("/[]/u","'",$text);
						$text = preg_replace("/[]/u",'"',$text);
						$text = str_replace("","-",$text);
						$text = str_replace(" "," ",$text);
						$text = str_replace("","c",$text);
						$text = str_replace("","C",$text);
						$text = str_replace("","n",$text);
						$text = str_replace("","N",$text);

						//2) Translation CP1252. &ndash; => -
						$trans = get_html_translation_table(HTML_ENTITIES); 
						$trans[chr(130)] = '&sbquo;';    // Single Low-9 Quotation Mark 
						$trans[chr(131)] = '&fnof;';    // Latin Small Letter F With Hook 
						$trans[chr(132)] = '&bdquo;';    // Double Low-9 Quotation Mark 
						$trans[chr(133)] = '&hellip;';    // Horizontal Ellipsis 
						$trans[chr(134)] = '&dagger;';    // Dagger 
						$trans[chr(135)] = '&Dagger;';    // Double Dagger 
						$trans[chr(136)] = '&circ;';    // Modifier Letter Circumflex Accent 
						$trans[chr(137)] = '&permil;';    // Per Mille Sign 
						$trans[chr(138)] = '&Scaron;';    // Latin Capital Letter S With Caron 
						$trans[chr(139)] = '&lsaquo;';    // Single Left-Pointing Angle Quotation Mark 
						$trans[chr(140)] = '&OElig;';    // Latin Capital Ligature OE 
						$trans[chr(145)] = '&lsquo;';    // Left Single Quotation Mark 
						$trans[chr(146)] = '&rsquo;';    // Right Single Quotation Mark 
						$trans[chr(147)] = '&ldquo;';    // Left Double Quotation Mark 
						$trans[chr(148)] = '&rdquo;';    // Right Double Quotation Mark 
						$trans[chr(149)] = '&bull;';    // Bullet 
						$trans[chr(150)] = '&ndash;';    // En Dash 
						$trans[chr(151)] = '&mdash;';    // Em Dash 
						$trans[chr(152)] = '&tilde;';    // Small Tilde 
						$trans[chr(153)] = '&trade;';    // Trade Mark Sign 
						$trans[chr(154)] = '&scaron;';    // Latin Small Letter S With Caron 
						$trans[chr(155)] = '&rsaquo;';    // Single Right-Pointing Angle Quotation Mark 
						$trans[chr(156)] = '&oelig;';    // Latin Small Ligature OE 
						$trans[chr(159)] = '&Yuml;';    // Latin Capital Letter Y With Diaeresis 
						$trans['euro'] = '&euro;';    // euro currency symbol 
						ksort($trans); 

						foreach ($trans as $k => $v) {
							$text = str_replace($v, $k, $text);
						}

						// 3) remove <p>, <br/> ...
						$text = strip_tags($text); 

						// 4) &amp; => & &quot; => '
						$text = html_entity_decode($text);

						// 5) remove Windows-1252 symbols like "TradeMark", "Euro"...
						$text = preg_replace('/[^(\x20-\x7F)]*/','', $text); 

						$targets=array('\r\n','\n','\r','\t');
						$results=array(" "," "," ","");
						$text = str_replace($targets,$results,$text);

						//XML compatible
						/*
						$text = str_replace("&", "and", $text);
						$text = str_replace("<", ".", $text);
						$text = str_replace(">", ".", $text);
						$text = str_replace("\\", "-", $text);
						$text = str_replace("/", "-", $text);
						*/

						return ($text);
					}

public function getImportManufacturerId($manufacturer_name) {
		$query = $this->db->query("SELECT manufacturer_id FROM " . DB_PREFIX . "manufacturer WHERE name LIKE '" . $manufacturer_name . "'");
        if($query->row)
            return $query->row['manufacturer_id'];
        else
            return '';
	}

